import React, { Component, useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import ReactPlayer from 'react-player';
import device from 'current-device'
// import { beFull, isFull } from 'be-full';
import { words, wordsToPhone } from './data'
import './index.css'



const Present = props => {

  const [isStart, setIsStart] = useState(true)
  const [flag, setFlag] = useState(1)
  const timer = useRef()

  function randomNum(min, max) {
    var num = (Math.random() * (max - min + 1) + min).toFixed(2);
    return num;
  }

  const init = (words) => {
    let container = document.querySelector('.container');
    console.log('====', container);
    let f = document.createDocumentFragment();
    words.forEach(w => {
      let word_box = document.createElement('div');
      let word = document.createElement('div');
      word.innerText = w;
      word.classList.add('word');
      word.style.color = '#FFFFFF';
      word.style.fontFamily = '楷体';
      word.style.fontSize = '20px'
      word_box.classList.add('word-box');
      word_box.style.setProperty("--margin-top", randomNum(-40, 20) + 'vh');
      word_box.style.setProperty("--margin-left", randomNum(6, 35) + 'vw');
      word_box.style.setProperty("--animation-duration", randomNum(15, 40) + 's');
      word_box.style.setProperty("--animation-delay", randomNum(-20, 0) + 's');

      word_box.appendChild(word);
      f.appendChild(word_box);


    })
    container.appendChild(f);
  }

  useEffect(() => {
    console.log('进入了这个页面');
    let wordsArr = wordsToPhone;
    if (device.desktop()) {
      console.log('电脑')
      wordsArr = words
    }
    init(wordsArr);
    setIsStart(false)
    setTimeout(() => {
      setIsStart(true);
    }, 1500)
    setTimeout(() => {
      // console.log('是否全屏', isFull(document.getElementById('sky')));
      let textone = document.querySelector('.textone').querySelector('h1');
      let text = document.querySelector('.text').querySelector('h1');
      textone.innerHTML = '今晚，整片星空将为你一人闪烁';
      textone.style.color = '#E8F9FD';
      textone.style.fontFamily = '华文楷体'
      text.innerHTML = '';
    }, 10000)
  }, [])

  return (
    <>
      <div className="sky">

        <div className="videofilm">
          <ReactPlayer
            width="100%"
            height="100%"
            url={props.videoSrc}
            playing={isStart}
            loop={true}
            volume={0.5}
            style={{ objectFit: 'cover' }}
          // onReady={(e) => { console.log('准备好了', e); setIsStart(true) }}
          />
        </div>
        <div className="textone">
          <h1>look at the stars</h1>
        </div>
        <div className="text">
          <h1>look how they shine for u</h1>
        </div>

        <div className="container textContainer"></div>
      </div>
    </>
  )
}

export default Present;
